public class UtilisationCalculatrice {

	/**
	 * @param args
	 */
	
	static int chiffre=3;
	
	
	public static int carre (int n) {
		n*=n;
		return n;
	}
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.print(carre(chiffre));

	}
}
